import React, { useState } from "react";

import {connect} from 'react-redux';
import { addToCart, adjustQty, removeFromCart } from "../../../redux/Shopping/shopping-actions";


const CartItem = ({ item, button, adjustQty, removeFromCart, addToCart }) => {
  const [input, setInput] = useState(item.qty);
  
  const onChangeHandler = (e) => {
    setInput(e.target.value);
    adjustQty(item.foto, e.target.value);
  };

//Logica del input QTY
  const [count, setCount] = useState(0);

  const decrementCount = () => {
    if (count > 0) setCount(count - 1);
  };

  const incrementCount = () => {
    setCount(count + 1);
  };
  

  return (

    <div className="card">
      <div className="row">
        <div className="col-7 img-container">
            <img src={`assets/img/${ item.foto }`} className="card-img-top" alt="..." />
        </div>

        <div className="col-5">
          <div className= "card-body">
            <h5 className="card-title fs-4">{item.nombre}</h5>
            <p className="card-text">${item.precio}</p>
            <div className="">
              <label htmlFor="qty">Qty</label>
              <input
                min="1"
                type="number"
                id="qty"
                name="qty"
                value={input}
                onChange={onChangeHandler}
              />
            </div>
            <a
              className="btn btn-outline-danger"
              href="#/"
              onClick={() => removeFromCart(item.foto)}
            >
              <i className="bi bi-trash"></i>
            </a>
            {
                (button) 
                    &&
                    (<button 
                        onClick={() => addToCart(item.foto)}
                        className="btn btn-lg"
                    >
                        AGREGAR AL CARRITO
                    </button>)
            }
          </div>
          
        </div>


        <div>
      <button onClick={decrementCount}>Click to decrease by 1</button>
        <input
        type="number"
        name="clicks"
        value={count}
        onChange={event => {
          setCount(event.target.value);
        }}
        />
        <button onClick={incrementCount}>Click to increase by 1</button>
    </div>
        
        
      </div>
      
      <hr />
    </div>



    
    
    
    

    // <div className="card">
    //   <img
    //     className=""
    //     src={`assets/img/${ item.foto }`}
    //     alt={item.title}
    //   />
    //   <div className="">
    //     <p className="">{item.nombre}</p>
    //     <p className="">$ {item.precio}</p>
    //   </div>
    //   <div className="">
    //     <div className="">
    //       <label htmlFor="qty">Qty</label>
    //       <input
    //         min="1"
    //         type="number"
    //         id="qty"
    //         name="qty"
    //         value={input}
    //         onChange={onChangeHandler}
    //       />
    //     </div>
    //     <button
    //       onClick={() => removeFromCart(item.foto)}
    //       className=""
    //     >
    //       <img
    //         src="https://image.flaticon.com/icons/svg/709/709519.svg"
    //         alt=""
    //       />
    //     </button>
    //   </div>
    // </div>
    
    
  
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    adjustQty: (id, value) => dispatch(adjustQty(id, value)),
    addToCart: (id) => dispatch(addToCart(id)),
    removeFromCart: (id) => dispatch(removeFromCart(id)),
  };
};

export default connect(null, mapDispatchToProps)(CartItem);
