import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"


// import Swiper core and required modules
import SwiperCore, {
  Pagination
} from 'swiper/core';


import { shoesData } from '../../shoesData/zapato'

import './slider.css';


// install Swiper modules
SwiperCore.use([Pagination]);

console.log(shoesData);



export const Slider = () => {
    return (
        <>
            <Swiper slidesPerView={3} spaceBetween={30} pagination={{"clickable": true}} className="mySwiper">
              {
                shoesData.map( (item, i) => (
                  <SwiperSlide key={ i }>
                    <img className="main-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                    <br />
                    
                    <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                    <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                    <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />

                    <h4>{item.nombre}</h4>
                    
                  </SwiperSlide>
                ) )
              }
              
            </Swiper>
        </>
    )
}
