import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"


// import Swiper core and required modules
import SwiperCore, {
  Pagination
} from 'swiper/core';


import zapato from '../../assets/img/6.jpg';

import './slider.css';


// install Swiper modules
SwiperCore.use([Pagination]);


export const Slider = () => {
    return (
        <>
            <Swiper slidesPerView={3} spaceBetween={30} pagination={{"clickable": true}} className="mySwiper">
              <SwiperSlide>
                <img className="main-img" src={ zapato } alt="zapato" />
                <br />
                
                <img className="thumbnail-img" src={ zapato } alt="zapato" />
                <img className="thumbnail-img" src={ zapato } alt="zapato" />
                <img className="thumbnail-img" src={ zapato } alt="zapato" />
              </SwiperSlide>
              <SwiperSlide>Slide 2</SwiperSlide>
              <SwiperSlide>Slide 3</SwiperSlide>
              <SwiperSlide>Slide 4</SwiperSlide>
              <SwiperSlide>Slide 5</SwiperSlide>
              <SwiperSlide>Slide 6</SwiperSlide>
              <SwiperSlide>Slide 7</SwiperSlide>
              <SwiperSlide>Slide 8</SwiperSlide>
              <SwiperSlide>Slide 9</SwiperSlide>
            </Swiper>
        </>
    )
}
