import React, { useState, useEffect } from "react";
import styles from "./Cart.module.css";

import { connect } from "react-redux";

import CartItem from "./CartItem/CartItem";
import { Link } from "react-router-dom";

const Cart = ({ cart }) => {
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalItems, setTotalItems] = useState(0);
  
  

  console.log(cart)

  useEffect(() => {
    let items = 0;
    let price = 0;

    cart.forEach((item) => {
      items += item.qty;
      price += item.qty * item.precio;
    });

    setTotalItems(items);
    setTotalPrice(price);
  }, [cart, totalPrice, totalItems, setTotalPrice, setTotalItems]);

  return (



    // <div className="card" style={{width: "18rem"}}>
    //   <div className="img-container">
    //       <img src={`assets/img/${ item.foto }`} className="card-img-top" alt="..." />
    //   </div>
    //   <div className= "card-body">
    //   <div className="thumbnail-container">
    //       <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
    //       <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
    //       <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
    //   </div>
    //   <h5 className="card-title">{item.nombre}</h5>
    //   <p className="card-text">${item.precio}</p>
    //   {
    //       (button) 
    //           &&
    //           (<button 
    //               onClick={() => addToCart(item.foto)}
    //               className="btn btn-lg"
    //           >
    //               AGREGAR AL CARRITO
    //           </button>)
    //   }
    //   </div>
    //   <hr />
    // </div>
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    <div className="">
      <div className="">
        {cart.map((item) => (
          <CartItem key={item.foto} item={item} />
        ))}
      </div>
      <div className="">
        <h4 className="">Cart Summary</h4>
        <Link to="/">
          <button className="btn btn-primary w-100">
            Ir a inicio
          </button>
        </Link>
        <div className="">
          <span>TOTAL: ({totalItems} items)</span>
          <span>$ {totalPrice}</span>
        </div>
        <button className="btn btn-success">
          Proceed To Checkout
        </button>
      </div>
    </div>



  );
};

const mapStateToProps = (state) => {
  return {
    cart: state.shop.cart,
  };
};

export default connect(mapStateToProps)(Cart);
