import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"


// import Swiper core and required modules
import SwiperCore, {
  Pagination
} from 'swiper/core';


import { shoesData } from '../../shoesData/zapato'

import './slider.css';
import { connect } from "react-redux";
import { addToCart } from "../../redux/Shopping/shopping-actions";


// install Swiper modules
SwiperCore.use([Pagination]);

console.log(shoesData);



const Slider = ({ addToCart }) => {
    return (
        <>
          <div className="container sliders-container">

            <Swiper slidesPerView={3} spaceBetween={30} className="mySwiper">
              {
                shoesData.map( (item, i) => (
                  <SwiperSlide key={ i }>
                    <div className="card" style={{width: "18rem"}}>
                      <div className="img-container">
                        <img src={`assets/img/${ item.foto }`} className="card-img-top" alt="..." />
                      </div>
                      <div className="card-body">
                        <div className="thumbnail-container">
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                        </div>
                        <h5 className="card-title">{item.nombre}</h5>
                        <p className="card-text">{item.precio}</p>
                        <button className="btn btn-lg">AÃ‘ADIR AL CARRITO</button>
                      </div>
                    </div>
                  </SwiperSlide>
                ) )
              }
              
            </Swiper>

            <h3>PRODUCTOS RECOMENDADOS</h3>
            <hr />

            <Swiper slidesPerView={3} spaceBetween={30} className="mySwiper">
              {
                shoesData.map( (item, i) => (
                  <SwiperSlide key={ i }>
                    <div className="card" style={{width: "18rem"}}>
                      <div className="img-container">
                        <img src={`assets/img/${ item.foto }`} className="card-img-top" alt="..." />
                      </div>
                      <div className="card-body">
                        <div className="thumbnail-container">
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                          <img className="thumbnail-img" src={`assets/img/${ item.foto }`} alt="zapato" />
                        </div>
                        <h5 className="card-title">{item.nombre}</h5>
                        <p className="card-text">{item.precio}</p>
                      </div>
                    </div>
                    <hr />
                  </SwiperSlide>
                ) )
              }
              
            </Swiper>
            
          </div>
        </>
    )
};


const mapDispatchToProps = dispatch => {
  return {
    addToCart: (id) => dispatch(addToCart(id)),
  }
}

export default connect(null, mapDispatchToProps)(Slider);
